import React from "react";
import BurgerIngredient from "../BurgerIngredient/BurgerIngredient";
import "./Burger.css";

const Burger = (props) => {
  var ingredients = [];

  // for (var key in props.ingredients) {
  //   const count = props.ingredients[key];

  //   for (var i = 0; i < count; i++) {
  //     ingredients.push(<BurgerIngredient type={key} />);
  //   }
  // }

  const objectKeys = Object.keys(props.ingredients);
  ingredients = objectKeys.map((key) => {
    return [...Array(props.ingredients[key])].map((value, index) => {
      return <BurgerIngredient key={key + index} type={key} />;
    });
  });

  return (
    <div className="Burger">
      <BurgerIngredient type="bread-top" />
      {ingredients}
      <BurgerIngredient type="bread-bottom" />
    </div>
  );
};

export default Burger;
